import { REMOVE_TARGET, REMOVE_SOURCE, ADD_TARGET, ADD_SOURCE } from '../actions/registry.js';

function reduce(state = 0, action) {
    switch(action.type){
        case ADD_SOURCE:
        case ADD_TARGET:
            return state + 1;
        case REMOVE_SOURCE:
        case REMOVE_TARGET:
            return state - 1;
        default:
            return state;
    }
}

export { reduce };
